<template>
  <div class="container">
    <NuxtLayout name="show"> </NuxtLayout>
    <!-- ！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！ -->
    <!-- 最上方img和右侧个人栏 -->
    <div style="text-align: center; margin-top: 10px" class="com-bar">
      <img height="40px" src="../assets/logo/logo2.png" alt="Your Image" />
      <a
        href="#"
        style="
          display: block;
          text-align: right;
          font-size: 30px;
          margin-top: 5px;
          margin-right: 10px;
        "
      >
        <!-- <el-icon><User /></el-icon> -->
        <img :src="userImg" alt="" />
      </a>
    </div>
    <!-- <div style="text-align: center; margin-top: 15px">
      <el-input
        style="border-radius: 15px; width: 85%"
        placeholder="搜症状/疾病/医生"
        @focus="isShow.value = false"
        @blur="isShow.value = true"
      />
    </div> -->
    <div style="text-align: center; margin-top: 15px" class="searchInput">
      <el-input
        style="border-radius: 15px"
        :style="{ width: isShow ? '75%' : '85%' }"
        @focus="focusFun"
        placeholder="查看更多医生"
      >
        <template #prefix>
          <el-icon class="el-input__icon"><search /></el-icon>
        </template>
      </el-input>
      <el-button class="search-btn" v-if="isShow">搜索</el-button>
    </div>
    <!-- 预约展示区 -->
    <div
      class="registion-show"
      v-for="(item, index) in appointmentsData"
      :key="index"
    >
      <div
        style="
          font-size: 14px;
          display: flex;
          justify-content: space-between;
          padding: 15px;
        "
      >
        <span style="color: #848496">我的预约</span>
        <span style="color: #3fa162">联系医生</span>
      </div>
      <!-- ！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！！ -->
      <!-- 展示个人最近的预约 -->
      <div class="registion-latest">
        <el-row :gutter="20" style="margin-left: 15px; margin-top: 5px">
          <el-col :span="12"
            ><div class="grid-content ep-bg-purple" />
            <span>姓名:{{ item.user.username }}</span></el-col
          >
          <el-col :span="12"
            ><div class="grid-content ep-bg-purple" />
            <span>医生:{{ item.doctor.name }}</span></el-col
          >
        </el-row>
        <el-row :gutter="20" style="margin-left: 15px; margin-top: 5px">
          <el-col :span="24"
            ><div class="grid-content ep-bg-purple" />
            <span>时间:{{ item.appointmentDate }}</span></el-col
          >
        </el-row>
      </div>
    </div>
